import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ProductService } from '../core/services/product.service';
import { CartService } from '../core/services/cart.service';
import { Product } from '../core/models/product.model';

@Component({
  selector: 'app-product-grid',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './product-grid.component.html',
  styleUrls: ['./product-grid.component.css']
})
export class ProductGridComponent implements OnInit {
  products: Product[] = [];
  page = 1;
  pageSize = 12;
  totalProducts = 0;
  loading = false;
  error: string | null = null;

  // filters/controls
  search = '';
  pageSizeOptions = [8, 12, 24, 48];

  constructor(private productService: ProductService, private cart: CartService) {}

  ngOnInit() {
    this.loadProducts();
  }

  get totalPages(): number {
    return Math.max(1, Math.ceil(this.totalProducts / this.pageSize));
  }

  loadProducts() {
    this.loading = true;
    this.error = null;
    this.productService.getProducts(this.page, this.pageSize).subscribe({
      next: (res) => {
        this.products = (res.items ?? []).map(p => ({ ...p, imageUrl: p.imageUrl ?? 'assets/ipl1.png' }));
        this.totalProducts = res.totalCount ?? this.products.length;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load products';
        this.loading = false;
        console.error(err);
      }
    });
  }

  onSearch() {
    if (!this.search.trim()) {
      this.page = 1;
      this.loadProducts();
      return;
    }
    this.loading = true;
    this.error = null;
    this.productService.searchProducts(this.search).subscribe({
      next: (items) => {
        this.products = (items ?? []).map(p => ({ ...p, imageUrl: p.imageUrl ?? 'assets/ipl1.png' }));
        this.totalProducts = this.products.length;
        this.page = 1;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Search failed';
        this.loading = false;
        console.error(err);
      }
    });
  }

  onPageChange(page: number) {
    if (page < 1 || page > this.totalPages) return;
    this.page = page;
    this.loadProducts();
  }

  onPageSizeChange() {
    this.page = 1;
    this.loadProducts();
  }

  addToCart(p: Product) {
    this.cart.add({
      productId: p.id,
      name: p.name ?? 'Product',
      price: p.price,
      imageUrl: p.imageUrl ?? 'assets/ipl1.png'
    }, 1);
  }
}
<div class="product-grid-container">
  <div class="grid-toolbar">
    <input [(ngModel)]="search" (keyup.enter)="onSearch()" placeholder="Search products" />
    <button (click)="onSearch()">Search</button>
    <label>
      Page size:
      <select [(ngModel)]="pageSize" (change)="onPageSizeChange()">
        <option *ngFor="let s of pageSizeOptions" [value]="s">{{ s }}</option>
      </select>
    </label>
  </div>

  <div *ngIf="loading">Loading products…</div>
  <div *ngIf="error" class="error">{{ error }}</div>

  <div class="product-grid" *ngIf="!loading && !error">
    <div class="product-card" *ngFor="let product of products">
      <img [src]="product.imageUrl || 'assets/ipl1.png'" [alt]="product.name || 'Product'" [title]="product.name || 'Product'" />
      <div class="product-info">
        <h3>{{ product.name }}</h3>
        <p class="team" *ngIf="product.franchiseName">{{ product.franchiseName }}</p>
        <p class="price">₹{{ product.price }}</p>
      </div>
      <button class="buy-btn" (click)="addToCart(product)">Add to Cart</button>
    </div>
  </div>

  <div class="pagination" *ngIf="!loading && !error && totalPages > 1">
    <button (click)="onPageChange(page-1)" [disabled]="page === 1">Prev</button>
    <span>Page {{page}} of {{totalPages}}</span>
    <button (click)="onPageChange(page+1)" [disabled]="page === totalPages">Next</button>
  </div>
</div>
