import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CommonModule } from '@angular/common';
import { ProductService } from '../core/services/product.service';
import { CartService } from '../core/services/cart.service';
import { Product } from '../core/models/product.model';

@Component({
  selector: 'app-product-detail',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  product: Product | null = null;
  loading = false;
  error: string | null = null;
  selectedImage: string | null = null;

  constructor(
    private route: ActivatedRoute,
    private productService: ProductService,
    private cart: CartService
  ) {}

  ngOnInit() {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loading = true;
      this.productService.getProductById(id).subscribe({
        next: (p: Product) => {
          this.product = p;
          if (p.images && p.images.length > 0) {
            this.selectedImage = p.images[0];
          } else {
            this.selectedImage = p.imageUrl || 'assets/ipl1.png';
          }
          this.loading = false;
        },
        error: (err: any) => {
          this.error = 'Product not found.';
          this.loading = false;
        }
      });
    }
  }

  selectImage(img: string) {
    this.selectedImage = img;
  }

  addToCart(product: Product) {
    this.cart.add({
      productId: product.id,
      name: product.name ?? 'Product',
      price: product.price,
      imageUrl: this.selectedImage || product.imageUrl || 'assets/ipl1.png'
    }, 1);
  }
}
<div class="product-detail-page">
  <div class="product-detail-card" *ngIf="product">
    <div class="product-images">
      <img class="product-main-image" [src]="selectedImage || product.imageUrl || 'assets/ipl1.png'" [alt]="product.name" />
      <div class="product-thumbnails" *ngIf="product.images?.length > 1">
        <img *ngFor="let img of product.images" class="product-thumbnail" [class.selected]="img === selectedImage" [src]="img" [alt]="product.name" (click)="selectImage(img)" />
      </div>
    </div>
    <div class="product-detail-info">
      <h2>{{ product.name }}</h2>
      <p class="team" *ngIf="product.franchiseName">Franchise: {{ product.franchiseName }}</p>
      <p class="price">₹{{ product.price }}</p>
      <p class="desc" *ngIf="product.description">{{ product.description }}</p>
      <button (click)="addToCart(product)">Add to Cart</button>
    </div>
  </div>
  <div *ngIf="loading">Loading product…</div>
  <div *ngIf="error" class="error">{{ error }}</div>
</div>
