import { Component } from '@angular/core';
import { RouterModule } from '@angular/router';
import { CartService } from '../core/services/cart.service';
import { map } from 'rxjs';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-main-layout',
  standalone: true,
  imports: [RouterModule, CommonModule],
  templateUrl: './main-layout.component.html',
  styleUrls: ['./main-layout.component.css']
})
export class MainLayoutComponent {
  cartCount$ = this.cart.state$.pipe(map(s => s.items.reduce((sum, i) => sum + i.quantity, 0)));
  constructor(public cart: CartService) {}
}
<nav class="navbar">
  <div class="navbar-left">
    <img src="assets/ipl-logo.png" alt="IPL Logo" class="logo" />
    <a routerLink="/" class="nav-link">Home</a>
    <a routerLink="/products" class="nav-link">Products</a>
    <a routerLink="/teams" class="nav-link">Team Info</a>
  </div>
  <div class="navbar-right">
    <a routerLink="/cart" class="nav-link cart-link">
      🛒
      <span class="cart-badge" *ngIf="(cartCount$ | async) as count">{{ count }}</span>
    </a>
    <ng-container *ngIf="false; else login">
      <span class="user-icon">👤</span>
      <span class="user-name">User</span>
    </ng-container>
    <ng-template #login>
      <a routerLink="/login" class="nav-link login-link">Login</a>
    </ng-template>
  </div>
</nav>
<main class="main-content">
  <router-outlet></router-outlet>
</main>
