import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CommonModule } from '@angular/common';
import { ProductService } from '../core/services/product.service';
import { CartService } from '../core/services/cart.service';
import { Product } from '../core/models/product.model';

@Component({
  selector: 'app-product-detail',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  product: Product | null = null;
  loading = false;
  error: string | null = null;

  constructor(
    private route: ActivatedRoute,
    private productService: ProductService,
    private cart: CartService
  ) {}

  ngOnInit() {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loading = true;
      this.productService.getProductById(id).subscribe({
        next: (p) => {
          this.product = p;
          this.loading = false;
        },
        error: (err) => {
          this.error = 'Product not found.';
          this.loading = false;
        }
      });
    }
  }

  addToCart(product: Product) {
    this.cart.add({
      productId: product.id,
      name: product.name ?? 'Product',
      price: product.price,
      imageUrl: product.imageUrl ?? 'assets/ipl1.png'
    }, 1);
  }
}
<div class="product-detail-page">
  <div class="product-detail-card" *ngIf="product">
    <img [src]="product.imageUrl || 'assets/ipl1.png'" [alt]="product.name" />
    <div class="product-detail-info">
      <h2>{{ product.name }}</h2>
      <p class="team" *ngIf="product.franchiseName">Franchise: {{ product.franchiseName }}</p>
      <p class="price">Price: ₹{{ product.price }}</p>
      <p class="desc" *ngIf="product.description">{{ product.description }}</p>
      <button (click)="addToCart(product)">Add to Cart</button>
    </div>
  </div>
  <div *ngIf="loading">Loading product…</div>
  <div *ngIf="error" class="error">{{ error }}</div>
</div>
