import { Component } from '@angular/core';
import { RouterModule, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { CartService } from '../core/services/cart.service';
import { AuthService, AuthResponse } from '../core/services/auth.service';
import { map } from 'rxjs';
import { FooterComponent } from './footer.component';

@Component({
  selector: 'app-main-layout',
  standalone: true,
  imports: [RouterModule, CommonModule, FooterComponent],
  templateUrl: './main-layout.component.html',
  styleUrls: ['./main-layout.component.css']
})
export class MainLayoutComponent {
  cartCount$ = this.cart.state$.pipe(map(s => s.items.reduce((sum, i) => sum + i.quantity, 0)));
  user: AuthResponse | null = null;
  showMenu = false;

  constructor(public cart: CartService, public auth: AuthService, private router: Router) {
    this.auth.user$.subscribe(u => this.user = u);
  }

  logout() {
    this.auth.logout();
    this.router.navigate(['/login']);
  }

  goToProfile() {
    this.router.navigate(['/profile']);
  }

  goToOrders() {
    this.router.navigate(['/orders']);
  }
}
<nav class="navbar">
  <div class="navbar-left">
    <img src="assets/ipl-logo.png" alt="IPL Logo" class="logo" />
    <a routerLink="/" class="nav-link">Home</a>
    <a routerLink="/products" class="nav-link">Products</a>
    <a routerLink="/teams" class="nav-link">Team Info</a>
  </div>
  <div class="navbar-right">
    <a routerLink="/cart" class="nav-link cart-link">
      ðŸ›’
      <span class="cart-badge" *ngIf="(cartCount$ | async) as count">{{ count }}</span>
    </a>
    <ng-container *ngIf="user; else login">
      <div class="profile-menu-wrapper" (mouseleave)="showMenu=false">
        <span class="user-icon" (click)="showMenu=!showMenu">ðŸ‘¤</span>
        <span class="user-name">{{ user.username }}</span>
        <div class="profile-menu" *ngIf="showMenu">
          <button (click)="goToProfile()">Profile</button>
          <button (click)="goToOrders()">Order History</button>
          <button (click)="logout()">Logout</button>
        </div>
      </div>
    </ng-container>
    <ng-template #login>
      <a routerLink="/login" class="nav-link login-link">Login</a>
    </ng-template>
  </div>
</nav>
<main class="main-content">
  <router-outlet></router-outlet>
</main>
<app-footer></app-footer>
