import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CartService } from '../core/services/cart.service';
import { OrderService } from '../core/services/order.service';
import { FormsModule } from '@angular/forms';
import { OrderRequest } from '../core/models/order.model';

@Component({
  selector: 'app-cart',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  state$ = this.cart.state$;

  placing = false;
  placed = false;
  error: string | null = null;

  constructor(public cart: CartService, private orderService: OrderService) {}


  updateQty(id: number, qty: number) {
    this.cart.updateQuantity(id, qty);
  }


  remove(id: number) {
    this.cart.remove(id);
  }


  clear() {
    this.cart.clear();
  }

  placeOrder() {
    this.placing = true;
    this.error = null;
    this.placed = false;
  const items = this.cart.currentState.items.map(i => ({
      productId: i.productId.toString(),
      quantity: i.quantity,
      price: i.price
    }));
    const req: OrderRequest = { items };
    this.orderService.placeOrder(req).subscribe({
      next: () => {
        this.placing = false;
        this.placed = true;
        this.cart.clear();
      },
      error: err => {
        this.placing = false;
        this.error = 'Failed to place order.';
      }
    });
  }
}
<div class="cart-container" *ngIf="(state$ | async) as state">
  <h2>Your Cart</h2>
  <div *ngIf="!state.items.length">Your cart is empty.</div>
  <table *ngIf="state.items.length" class="cart-table">
    <thead>
      <tr>
        <th>Product</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Subtotal</th>
        <th></th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of state.items">
        <td>
          <img [src]="item.imageUrl || 'assets/ipl1.png'" alt="{{item.name}}" class="cart-img" />
          {{ item.name }}
        </td>
        <td>₹{{ item.price }}</td>
        <td>
          <input type="number" min="1" [(ngModel)]="item.quantity" (change)="updateQty(item.productId, item.quantity)" />
        </td>
        <td>₹{{ item.price * item.quantity }}</td>
        <td><button (click)="remove(item.productId)">Remove</button></td>
      </tr>
    </tbody>
  </table>
  <div *ngIf="state.items.length" class="cart-summary">
    <div>Total: <b>₹{{ cart.total }}</b></div>
    <button (click)="clear()">Clear Cart</button>
    <button 
      [disabled]="!state.items.length || placing"
      (click)="placeOrder()">
      {{ placing ? 'Placing Order...' : 'Place Order' }}
    </button>
    <div *ngIf="placed" class="order-success">Order placed successfully!</div>
    <div *ngIf="error" class="order-error">{{ error }}</div>
  </div>
</div>
