import { Component } from '@angular/core';
import { ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../core/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  standalone: true,
  imports: [ReactiveFormsModule, CommonModule]
})
export class LoginComponent {
  loginForm: FormGroup;
  loading = false;
  error: string | null = null;

  constructor(private fb: FormBuilder, private auth: AuthService, private router: Router) {
    this.loginForm = this.fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.loginForm.invalid) {
      // Mark all fields as touched to show validation errors
      Object.values(this.loginForm.controls).forEach(control => control.markAsTouched());
      return;
    }
    this.loading = true;
    this.error = null;
    this.auth.login(this.loginForm.value).subscribe({
      next: user => {
        this.router.navigate(['/']);
      },
      error: err => {
        this.error = err.error || 'Login failed';
        this.loading = false;
      },
      complete: () => this.loading = false
    });
  }

  goToRegister() {
    this.router.navigate(['/register']);
  }

  goToForgotPassword() {
    this.router.navigate(['/forgot-password']);
  }
}
<div class="login-container">
  <form [formGroup]="loginForm" (ngSubmit)="onSubmit()" class="login-form">
    <h2>Login</h2>
    <div class="form-group">
      <label for="username">Username</label>
      <input id="username" formControlName="username" type="text" />
      <div *ngIf="loginForm.get('username')?.touched && loginForm.get('username')?.invalid" class="validation-error">
        Username is required
      </div>
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input id="password" formControlName="password" type="password" />
      <div *ngIf="loginForm.get('password')?.touched && loginForm.get('password')?.invalid" class="validation-error">
        Password is required
      </div>
    </div>
    <div *ngIf="error" class="error">{{ error }}</div>
    <button type="submit" [disabled]="loading">Login</button>
    <div class="links">
      <button type="button" class="link-btn" (click)="goToRegister()">Don't have an account? Register</button>
      <button type="button" class="link-btn" (click)="goToForgotPassword()">Forgot Password?</button>
    </div>
  </form>
</div>
