import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-banner',
  standalone: true,
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  @Input() images: { url: string; caption?: string }[] = [];
  currentIndex = 0;
  intervalId: any;

  ngOnInit() {
          console.log('Banner images:', this.images);
    this.startRotation();
  }

  ngOnDestroy() {
    clearInterval(this.intervalId);
  }

  startRotation() {
    this.intervalId = setInterval(() => {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
    }, 3500);
  }

  goTo(index: number) {
    this.currentIndex = index;
  }
}
<div *ngIf="images && images.length > 0; else noImages" class="banner-container">
   Hello
   <div class="banner-image-wrapper">
    <img [src]="images[currentIndex].url" class="banner-image" alt="Banner image" />
    <div class="banner-caption" *ngIf="images[currentIndex].caption">
      {{ images[currentIndex].caption }}
    </div>
  </div>
  <div class="banner-dots">
    <span *ngFor="let img of images; let i = index"
          [class.active]="i === currentIndex"
          (click)="goTo(i)"></span>
  </div>
</div>
<ng-template #noImages>
  Hello Hello
  <div style="color: red;">No banner images available</div>
</ng-template>
